<template>
  <div class="side-nav-container">
    <div
      v-if="showTopBtn"
      class="scroll-top"
      @click="scrollToTop"
    />
    <div
      :class="['comments', {
        'comments--open': commentsDisplay
      }]"
      @click="clickComments"
    />
    <div
      v-if="userType === 1"
      class="talk"
      @click="clickTalk"
    />
    <div
      v-if="userType === 1"
      class="ask"
      @click="clickQuestion">
      <div
        v-if="showAskTip"
        class="ask__tip"
      />
    </div>

    <danmu
      :real-msg="realMsg"
      :pause="!commentsDisplay"
    />
  </div>
</template>

<script>
import get from 'lodash/get';
import Danmu from '../../../components/Danmu.vue';
import { getGlobalConfig } from '../../../utils/index';
import Bus from '../../../utils/bus';

const {
  userType,
} = getGlobalConfig();

export default {
  name: 'side-nav',

  components: {
    danmu: Danmu,
  },

  props: {
    realMsg: Object,
    showTopBtn: {
      type: Boolean,
    },
  },

  data() {
    return {
      commentsDisplay: true,
      userType,
      showAskTip: false,
    };
  },

  watch: {
    realMsg(newValue) {
      if (get(newValue, 'fromMsg.msgCat') === 1) {
        this.showAskTip = true;
      }
    },
  },

  created() {
    Bus.$on('user-type', (ev) => {
      if (typeof ev.userType !== 'undefined') {
        this.userType = ev.userType;
      }
    });

    // let index = 1;
    // setInterval(() => {
    //   index = index + 1;
    //   this.realMsg = {
    //     fromMsg: {
    //       content: 'sdsds' + index
    //     }
    //   };
    // }, 1000);
  },

  methods: {
    clickQuestion() {
      this.showAskTip = false;
      this.$emit('trigger-question');
    },
    clickTalk() {
      this.$emit('trigger-talk');
    },
    clickComments() {
      this.commentsDisplay = !this.commentsDisplay;
    },
    scrollToTop() {
      this.$emit('scroll-to-top');
    },
  },
};
</script>

<style lang="scss">
  @mixin icon {
    width: 30px;
    height: 30px;
    background-size: cover;
    margin-top: 10px;
    position: relative;
  }
  .side-nav-container {
    position: fixed;
    right: 15px;
    bottom: 65px;

    .ask {
      @include icon;

      background-image: url(data:image/png;base64,);

      &--point {
        background-image: url(data:image/png;base64,);
      }

      &__tip {
        width: 10px;
        height: 10px;
        background-image: url(data:image/png;base64,);
        background-size: cover;
        position: absolute;
        right: 0;
        top: 0;
      }
    }

    .talk {
      @include icon;

      background-image: url(data:image/png;base64,);
    }

    .comments {
      @include icon;

      background-image: url(data:image/png;base64,);

      &--open {
        background-image: url(data:image/png;base64,);
      }
    }

    .scroll-top {
      @include icon;

      background-image: url(data:image/png;base64,);
    }
  }
</style>
